import React from 'react';
import { useState, useEffect } from 'react';
import './Search.css';
const baseUrl = process.env.REACT_APP_API_URL;

const Search = () => {

    const [location, setLocation] = useState([]);

    useEffect(() => {
        fetch(`${baseUrl}/location`, {method:'GET'})
        .then((res) => res.json())
        .then((data) => {
            setLocation(data);
        })
    },[])

    const renderCity = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <option key={item.state_id}>
                        {item.state}
                    </option>
                )
            })
        } 
    }

    const handleCity = (Event) => {
        console.log(event.target.value);
    }

    return(
        <div className="container1">
        <img src="./images/food-wallpaper.png" alt="" className="mainimg"/>
        <div className="logo">
            <div className="roter">
                <img src="./images/healthy-plate_meals.jpg" alt=""/>
                <h3>HI</h3>
            </div> 
        </div>
        <div className="heading">
            Find Best Place Near You
        </div>
        <div className="dropdown">
            <select onChange={handleCity}>
                <option>-----SELECT YOUR CITY-----</option>
                {renderCity(location)}
            </select>
            <select>
                <option>-----SELECT YOUR RESTAURANTS-----</option>
                <option>Wow Momos</option>
            </select>
        </div>
    </div>
    )
}
export default Search;
