import React from 'react';
import { useState,useEffect } from 'react';
import './Listing.css';
import {useParams} from 'react-router-dom';
import axios from 'axios';
import ListingDisplay from './ListingDisplay';
import cuisineFilter from '../FIlter/cuisineFilter';

const baseUrl = process.env.REACT_APP_API_URL;

const ListingLogic = () => {

    const [restaurantList, setRestaurantList] = useState([]);

    let params = useParams();
    let mealId = params.mealId;

    useEffect(() => {
        sessionStorage.setItem('mealId',mealId)
        axios.get(`${baseUrl}/restaurants?mealId=${mealId}`)
        .then((res) => {
            setRestaurantList(res.data)
        })
    }, [mealId])


    return(
        <div className="container">
            <div className="selection">
                <div className="">
                    <cuisineFilter />
                    {/* <div className="filter2">
                        <h2>Price filter</h2>
                        <form action="">
                            <div className="select"><input type="radio" value=""/><p>All</p></div>
                            <div className="select"><input type="radio" value="1"/><p>0-300</p></div>
                            <div className="select"><input type="radio" value="2"/><p>301-600</p></div>
                            <div className="select"><input type="radio" value="3"/><p>601-900</p></div>
                            <div className="select"><input type="radio" value="4"/><p>901-5000</p></div>
                        </form>
                    </div> */}
                </div>
                <div className="apply"><button>Apply</button></div>
            </div>
            <ListingDisplay listData={restaurantList}/>
        </div>
    )
}

export default ListingLogic;
