import React from 'react';
import './Header.css';
const Header = () => {
    const LightDark = () => {
        // alert("ncdcjd");
        let d = document.getElementById('root');
        let gg = document.getElementsByClassName('fff');
        
        d.classList.toggle('mylight');
        // d.style.backgroundColor("black");
        let mybody = document.body;
        mybody.classList.toggle('mylight');
    }
    return(
        <div>
            <header>
                <div className="title"><h2>Zomato</h2></div>
                <div className="autentication">
                    <i class="fa-solid fa-moon" onClick={LightDark()} id='litdar'></i>
                    <button>LogIn</button>
                    <button>Sign In</button>
                </div>
            </header>
        </div>
    )
}
export default Header;
