import React, { useEffect, useState } from 'react';
import './QuickSearch.css';
import QuickDisplay from './QuickDisplay';

const baseUrl = process.env.REACT_APP_API_URL;


const QuickSearch = () => {

    const [mealType, setmealType] = useState([]);

    useEffect(() => {
        fetch(`${baseUrl}/mealType`, {method:'GET'})
        .then((res) => res.json())
        .then((data) => {
            setmealType(data);
        })
    }, [])

    const listMeal = ({mealData}) => {
        if(mealData){
            return mealData.map((item) => {
                return(
                    <Link to={`/listing/${item.mealtype_id}`}>
                        <div className="types">
                            <img src={item.meal_image} alt=""/>
                            <div className="type-cont">
                                <p href="./page2.html" className='heading'>{item.mealtype}</p>
                                <p>Best Deal for {item.mealtype}</p>
                            </div>
                        </div>
                    </Link>
                )
            })
        }
    }

    return(
        <div className="container2">
            <h2>Quick Search</h2>
            <p>Find Restaurants by MealType</p>
            <QuickDisplay mealData={mealType}/>
        </div>
    )
}
export default QuickSearch;
